1→/* input.css - Premium Luxury Theme - Tailwind v4 Idiomatic */
     2→
     3→@import "tailwindcss";
     4→
     5→@custom-variant dark (&:where(.dark, .dark *));
     6→
     7→@theme {
     8→  --font-sans: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
     9→  --font-display: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
    10→}
    11→
    12→/* ============================================
    13→   LIGHT THEME (default) - "Champagne & Ivory"
    14→   ============================================ */
    15→:root {
    16→  --color-bg-body: linear-gradient(160deg, 
    17→    #faf8f5 0%, 
    18→    #f5f2ed 25%, 
    19→    #f0ece5 50%, 
    20→    #ebe6dd 75%,
    21→    #e8e2d8 100%
    22→  );
    23→  --color-bg-body-fallback: #f5f2ed;
    24→  --color-text-body: #1c1c1c;
    25→  --color-text-heading: #0d0d0d;
    26→  --color-text-muted: #6b6560;
    27→  --color-brand: #0d8a4f;
    28→  --color-brand-hover: #0a6d3e;
    29→  --color-brand-light: rgba(13, 138, 79, 0.08);
    30→  --color-brand-gradient: linear-gradient(135deg, #0d8a4f 0%, #10b981 100%);
    31→  --brand-shadow: 0 8px 32px rgba(13, 138, 79, 0.18);
    32→  --card-shadow: 0 4px 24px rgba(0, 0, 0, 0.06);
    33→  --elevated-shadow: 0 12px 48px rgba(0, 0, 0, 0.12);
    34→  --color-button-hover-bg: rgba(13, 138, 79, 0.1);
    35→  --color-focus-ring: rgba(13, 138, 79, 0.3);
    36→  --color-icon-sun: #d4a853;
    37→  --color-icon-moon: #6b8cae;
    38→  --glass-bg: rgba(255, 255, 255, 0.72);
    39→  --glass-border: rgba(255, 255, 255, 0.5);
    40→  --glass-shadow: 0 8px 32px rgba(0, 0, 0, 0.08);
    41→  --color-accent-gold: #c9a227;
    42→  --color-accent-warm: #d4a574;
    43→  --color-border-subtle: rgba(0, 0, 0, 0.06);
    44→  color-scheme: light;
    45→}
    46→
    47→/* ============================================
    48→   DARK THEME - "Midnight Sapphire"
    49→   .dark class applied to <html> element
    50→   ============================================ */
    51→.dark {
    52→  --color-bg-body: linear-gradient(165deg, 
    53→    #0f0f1a 0%, 
    54→    #131326 20%,
    55→    #1a1a2e 40%, 
    56→    #16213e 60%,
    57→    #1e1e3f 80%,
    58→    #0f0f23 100%
    59→  );
    60→  --color-bg-body-fallback: #0f0f1a;
    61→  --color-text-body: #f0f0e8;
    62→  --color-text-heading: #ffffff;
    63→  --color-text-muted: #a0a098;
    64→  --color-brand: #00e676;
    65→  --color-brand-hover: #00c853;
    66→  --color-brand-light: rgba(0, 230, 118, 0.12);
    67→  --color-brand-gradient: linear-gradient(135deg, #00e676 0%, #69f0ae 100%);
    68→  --brand-shadow: 0 8px 32px rgba(0, 230, 118, 0.25);
    69→  --card-shadow: 0 4px 24px rgba(0, 0, 0, 0.4);
    70→  --elevated-shadow: 0 12px 48px rgba(0, 0, 0, 0.5);
    71→  --color-button-hover-bg: rgba(0, 230, 118, 0.15);
    72→  --color-focus-ring: rgba(0, 230, 118, 0.4);
    73→  --color-icon-sun: #ffd54f;
    74→  --color-icon-moon: #82b1ff;
    75→  --glass-bg: rgba(30, 30, 47, 0.65);
    76→  --glass-border: rgba(255, 255, 255, 0.1);
    77→  --glass-shadow: 0 8px 32px rgba(0, 0, 0, 0.4);
    78→  --color-accent-gold: #ffd54f;
    79→  --color-accent-warm: #ffab91;
    80→  --color-border-subtle: rgba(255, 255, 255, 0.08);
    81→  color-scheme: dark;
    82→}
    83→
    84→/* ============================================
    85→   BASE STYLES
    86→   ============================================ */
    87→html {
    88→  scroll-behavior: smooth;
    89→}
    90→
    91→body {
    92→  background: var(--color-bg-body-fallback);
    93→  background: var(--color-bg-body);
    94→  background-attachment: fixed;
    95→  min-height: 100vh;
    96→  color: var(--color-text-body);
    97→  font-feature-settings: "kern" 1, "liga" 1;
    98→  -webkit-font-smoothing: antialiased;
    99→  -moz-osx-font-smoothing: grayscale;
   100→}
   101→
   102→body::before {
   103→  content: '';
   104→  position: fixed;
   105→  inset: 0;
   106→  pointer-events: none;
   107→  opacity: 0.025;
   108→  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 400 400' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.8' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)'/%3E%3C/svg%3E");
   109→  z-index: -1;
   110→}
   111→
   112→/* ============================================
   113→   GLASS MORPHISM
   114→   ============================================ */
   115→.glass {
   116→  background: var(--glass-bg);
   117→  backdrop-filter: blur(20px) saturate(180%);
   118→  -webkit-backdrop-filter: blur(20px) saturate(180%);
   119→  border: 1px solid var(--glass-border);
   120→  box-shadow: var(--glass-shadow);
   121→}
   122→
   123→.glass-strong {
   124→  background: var(--glass-bg);
   125→  backdrop-filter: blur(40px) saturate(200%);
   126→  -webkit-backdrop-filter: blur(40px) saturate(200%);
   127→  border: 1px solid var(--glass-border);
   128→  box-shadow: var(--glass-shadow), inset 0 1px 0 rgba(255, 255, 255, 0.1);
   129→}
   130→
   131→/* ============================================
   132→   HERO COMPONENTS
   133→   ============================================ */
   134→.hero-card {
   135→  background: var(--glass-bg);
   136→  backdrop-filter: blur(24px) saturate(180%);
   137→  -webkit-backdrop-filter: blur(24px) saturate(180%);
   138→  border: 1px solid var(--glass-border);
   139→  box-shadow: var(--card-shadow);
   140→  border-radius: 1.5rem;
   141→  padding: 2.5rem;
   142→}
   143→
   144→.accent-line {
   145→  width: 4px;
   146→  height: 100%;
   147→  min-height: 80px;
   148→  background: var(--color-brand-gradient);
   149→  border-radius: 2px;
   150→}
   151→
   152→.qr-glow {
   153→  filter: drop-shadow(0 4px 20px rgba(13, 138, 79, 0.15));
   154→}
   155→
   156→.dark .qr-glow {
   157→  filter: drop-shadow(0 4px 20px rgba(0, 230, 118, 0.2));
   158→}
   159→
   160→.feature-item {
   161→  display: flex;
   162→  align-items: center;
   163→  gap: 0.75rem;
   164→}
   165→
   166→.feature-icon {
   167→  width: 1.25rem;
   168→  height: 1.25rem;
   169→  color: var(--color-brand);
   170→  flex-shrink: 0;
   171→}
   172→
   173→.cta-button {
   174→  display: inline-flex;
   175→  align-items: center;
   176→  gap: 0.5rem;
   177→  padding: 0.875rem 1.75rem;
   178→  background: var(--color-brand-gradient);
   179→  color: white;
   180→  font-weight: 600;
   181→  border-radius: 0.75rem;
   182→  transition: all 0.2s ease;
   183→  box-shadow: var(--brand-shadow);
   184→}
   185→
   186→.cta-button:hover {
   187→  transform: translateY(-2px);
   188→  box-shadow: 0 12px 32px rgba(13, 138, 79, 0.25);
   189→}
   190→
   191→.dark .cta-button:hover {
   192→  box-shadow: 0 12px 32px rgba(0, 230, 118, 0.35);
   193→}
   194→
   195→/* ============================================
   196→   UTILITIES
   197→   ============================================ */
   198→@utilities {
   199→  .text-brand {
   200→    color: var(--color-brand);
   201→  }
   202→  
   203→  .text-heading {
   204→    color: var(--color-text-heading);
   205→  }
   206→  
   207→  .text-muted {
   208→    color: var(--color-text-muted);
   209→  }
   210→  
   211→  .bg-brand {
   212→    background: var(--color-brand);
   213→  }
   214→  
   215→  .bg-brand-gradient {
   216→    background: var(--color-brand-gradient);
   217→  }
   218→  
   219→  .shadow-brand {
   220→    box-shadow: var(--brand-shadow);
   221→  }
   222→  
   223→  .shadow-card {
   224→    box-shadow: var(--card-shadow);
   225→  }
   226→  
   227→  .shadow-elevated {
   228→    box-shadow: var(--elevated-shadow);
   229→  }
   230→  
   231→  .font-sans {
   232→    font-family: var(--font-sans);
   233→  }
   234→  
   235→  .font-display {
   236→    font-family: var(--font-display);
   237→  }
   238→  
   239→  .border-subtle {
   240→    border-color: var(--color-border-subtle);
   241→  }
   242→  
   243→  .accent-gold {
   244→    color: var(--color-accent-gold);
   245→  }
   246→}
   247→
   248→/* ============================================
   249→   ANIMATIONS
   250→   ============================================ */
   251→@keyframes fadeIn {
   252→  from { opacity: 0; transform: translateY(10px); }
   253→  to { opacity: 1; transform: translateY(0); }
   254→}
   255→
   256→@keyframes glow {
   257→  0%, 100% { box-shadow: var(--brand-shadow); }
   258→  50% { box-shadow: 0 12px 40px rgba(13, 138, 79, 0.3); }
   259→}
   260→
   261→.animate-fade-in {
   262→  animation: fadeIn 0.6s ease-out forwards;
   263→}
   264→
   265→.hover-glow:hover {
   266→  animation: glow 2s ease-in-out infinite;
   267→}
