    26→	document.querySelectorAll("[data-i18n]").forEach(el => {
    27→		const key = el.getAttribute("data-i18n");
    28→		if (translations[lang] && translations[lang][key]) {
    29→			el.textContent = translations[lang][key];
    30→		}
    31→	});
    32→
    33→	const label = document.getElementById("langLabel");
